import React, { useRef, useState } from "react";
import styled from "styled-components";
import Input from "./components/Input/Input";
import classes from "./App.module.css";
import Table from "./components/Table/Table";
import Modal from "./components/Modal/Modal";

const Dim = styled.div`
  z-index: 999;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.8);
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: -150px;
`;

function App() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [userInputs, setUserInputs] = useState([]);
  console.log("App * userInputs=====>", userInputs);
  const openModal = () => {
    setIsModalOpen(false);
  };
  const setUserHandler = (userInput) => {
    setUserInputs(userInput);
  };

  const closeModal = () => {
    setIsModalOpen(true);
  };

  return (
    <div className={classes.main}>
      {isModalOpen && (
        <Dim onClick={closeModal}>
          <Modal onClick={closeModal} />
        </Dim>
      )}
      <Input onClickBtn={setUserHandler} setIsModalOpen={setIsModalOpen} />
      <Table userInputs={userInputs} />
    </div>
  );
}

export default App;
