import React, { useState } from "react";
import styled from "styled-components";
import classes from "./Input.module.css";

const Container = styled.form`
  background-color: white;
  width: 70%;
  height: 300px;
  border-radius: 20px;
  display: flex;
  flex-direction: column;
  padding: 30px;
`;

const AddBtn = styled.button`
  margin-top: 20px;
  width: 100px;
  height: 40px;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 0.25rem;
  background: linear-gradient(180deg, #1f584b, #17493d);
  color: #c2e9e0;
  font-family: "Roboto Condensed", sans-serif;
  cursor: pointer;
`;

const initalState = {
  id: "",
  userName: "",
  age: "",
};

const Input = (props) => {
  const [userInput, setUserInput] = useState(initalState);

  const inputChangeHandler = (input, value) => {
    setUserInput((prevInput) => {
      return {
        ...prevInput,
        [input]: value,
        id: Math.random() + 1,
      };
    });
  };

  const isValidUserName = (userName) => {
    console.log(!!userName && /^[A-Za-z]+$/.test(userName));
    return !!userName && /^[A-Za-z]+$/.test(userName);
  };

  const isValidAge = (age) => {
    return !!age && !isNaN(age) && age > 0;
  };

  const submitHandler = (e) => {
    e.preventDefault();

    if (!isValidUserName(userInput.userName)) {
      console.log("Invalid userName");
      props.setIsModalOpen(true);
      return;
    }

    if (!isValidAge(userInput.age)) {
      console.log("Invalid age");
      props.setIsModalOpen(true);
      return;
    }

    if (isValidAge && isValidUserName) {
      props.onClickBtn((prev) => [...prev, userInput]);
      props.setIsModalOpen(false);
    }
  };
  return (
    <Container onSubmit={submitHandler}>
      <div className={classes["input-group"]}>
        <label htmlFor="userName">UserName</label>
        <input
          id="userName"
          type="text"
          value={userInput["userName"]}
          onChange={(e) => inputChangeHandler("userName", e.target.value)}
        />
        <label htmlFor="age">Age(Years)</label>
        <input
          id="age"
          type="text"
          value={userInput["age"]}
          onChange={(e) => inputChangeHandler("age", e.target.value)}
        />
      </div>
      <AddBtn onClick={submitHandler} type="submit">
        add
      </AddBtn>
    </Container>
  );
};

export default Input;
