import React, { useState } from "react";
import styled from "styled-components";
import classes from "./Input.module.css";

const Container = styled.form`
  background-color: white;
  width: 70%;
  height: 300px;
  border-radius: 20px;
  display: flex;
  flex-direction: column;
  padding: 30px;
`;

const AddBtn = styled.button`
  margin-top: 20px;
  width: 100px;
  height: 40px;
  padding: 0.5rem 1rem;
  border: none;
  border-radius: 0.25rem;
  background: linear-gradient(180deg, #1f584b, #17493d);
  color: #c2e9e0;
  font-family: "Roboto Condensed", sans-serif;
  cursor: pointer;
`;

const initalState = {
  userName: "",
  age: "",
};

const Input = (props) => {
  const [userInput, setUserInput] = useState(initalState);

  const inputChangeHandler = (input, value) => {
    props.setUserInput((prevInput) => {
      return {
        ...prevInput,
        [input]: +value,
      };
    });
  };
  const submitHandler = (e) => {
    e.preventDefault();
    props.setUserInputs(userInput);
  };
  return (
    <Container onClick={submitHandler}>
      <div className={classes["input-group"]}>
        <label htmlFor="user-name">UserName</label>
        <input
          id="user-name"
          type="text"
          value={props.userInput["user-name"]}
          onChange={(e) => inputChangeHandler("userName", e.target.value)}
        />
        <label htmlFor="age">Age(Years)</label>
        <input
          id="age"
          type="text"
          value={props.userInput["age"]}
          onChange={(e) => inputChangeHandler("age", e.target.value)}
        />
      </div>
      <AddBtn onClick={props.onClickBtn} type="submit">
        add
      </AddBtn>
    </Container>
  );
};

export default Input;
